
const sheet = new CSSStyleSheet

sheet.replaceSync(get_theme())
/******************************************************************************
  ICON COMPONENT
******************************************************************************/
var count = 0
const ID = __filename
const STATE = { ids: {}, hub: {} } // all state of component module
// ----------------------------------------

module.exports = icon_button

function icon_button (opts, protocol) {
  // ----------------------------------------
  // INSTANCE STATE & ID
  const id = `${ID}:${count++}` // assigns their own name
  const state = STATE.ids[id] = { wait: {}, hub: {}, aka: {} } // all state of component instance
  // ----------------------------------------
  let { src, src_active } = opts // opts - icon/img src
  // ----------------------------------------
  // protocol
  let parent_args
  if (!protocol) console.log("NO PROTOCOL")
  if (protocol) {
    parent_args = protocol(listen, id)
    function listen (message) {
      const { head,  refs, type, data, meta } = message
      const [by, to, id] = head
      ;(data === 'DEFAULT') ? icon_button.classList.add('active') : icon_button.classList.remove('active')
    }
  }
  // ----------------------------------------
  const el = document.createElement('div')
  const shadow = el.attachShadow({ mode: 'closed' })
  shadow.innerHTML = `<div class="icon_btn">${src}</div>`
  const [icon_button] = shadow.children
  // Toggle Icon
  let activeState = true
  icon_button.onclick = src_active ? toggle_class_and_src : toggle_class
  shadow.adoptedStyleSheets = [sheet]

  return el

  function toggle_class_and_src (e) {
    ;icon_button.innerHTML = activeState ? src_active : src
    activeState = !activeState
    toggle_class(e)
  }
  function toggle_class (e) {
    if (protocol) {
      const message = {
        head: { by: id, to: parent_args[1], mid: 0 },
        type: 'handle_page_change',
        data: 'DEFAULT'
      }
      parent_args[0](message)
    } else e.target.classList.toggle('active')
  }
}
function get_theme () {
  return`
    .icon_btn {
      display: flex;
      justify-content: center;
      align-items: center;
      height: 40px;
      box-sizing: border-box;
      aspect-ratio: 1/1;
      cursor: pointer;
      border: 1px solid var(--primary_color);
      background-color: var(--bg_color);
    }
    .icon_btn svg {
      height: 25px;
      width: 25px;
      pointer-events: none;
    }
    .icon_btn svg * {
      pointer-events: none;
    }
    .icon_btn.active {
      background-color: var(--ac-2)
    }
  `
}